import React, { useState } from 'react';
import { ImageBackground, View, Text, TextInput, StyleSheet, Button } from "react-native";

const image = { uri: "https://cdn.discordapp.com/attachments/806125256413937704/811897233582587914/Logo_-_1.png" }

const LoginScreen = ({ navigation }) => {


    return (
        <View style={styles.container}>
            <ImageBackground source={image} style={styles.image}>
                <Text style={styles.titre}>Bienvenue !</Text>
                <Text style={styles.titre}>Connectez-vous pour commencer !</Text>
                <TextInput placeholder="Email" />
                <TextInput placeholder="Mot de passe" />
                <Button
                    title="Se connecter"
                    onPress={() => navigation.navigate('Home')}
                />
                <Button
                    title="S'enregistrer"
                    onPress={() => navigation.navigate('CreateAccount')}
                />
            </ImageBackground>
        </View>
    );
};

export default LoginScreen;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 24,
        backgroundColor: "#eaeaea"
    },
    title: {
        marginTop: 16,
        paddingVertical: 8,
        borderWidth: 4,
        borderColor: "#20232a",
        borderRadius: 6,
        backgroundColor: "#61dafb",
        color: "#20232a",
        textAlign: "center",
        fontSize: 30,
        fontWeight: "bold"
    }
});
